        // Heart parametric equations
        const scale = size * 0.05;
        let x = 16 * Math.pow(Math.sin(t * Math.PI * 2), 3);
        let y = -(13 * Math.cos(t * Math.PI * 2) - 5 * Math.cos(2 * t * Math.PI * 2) - 
                  2 * Math.cos(3 * t * Math.PI * 2) - Math.cos(4 * t * Math.PI * 2));
        
