        scanLineSpeed: 0.01,      // Speed of scanning line
        scanLineWidth: 3,         // Width of scanning beam
        color: '#00CED1',         // Cyan for tech/biometric feel
        glowColor: '#00FFFF',     // Bright cyan for scan
